import styles from "./SearchBar.module.css"




export default function SearchBar({onSearch}) {//Destructuramos onSearch
   //Retornamos un div con el input tipo search y un botón al cual le pasaremos onSearch recibido por parametro y destructurado
      const [id, setId] = useState("");
      const inputRef = useRef(null);

      const handleChange =(event)=>{

            setId(event.target.value)

      }
      const handleAdd = () => {
         onSearch(id);
         setId("");
         inputRef.current.focus(); // Coloca el foco en el input después de hacer clic en "Agregar"
       };
   return (
      <div >          
          <input ref={inputRef}   type='search' onChange ={handleChange} value={id} />
          <button  onClick={(andleAdd)=>{onSearch(id); setId("")}}>Agregar</button> 

      </div>
   );
}
