import React from "react";
import {Link, NavLink} from "react-router-dom";
import styles from "./Card.module.css"
import {addFav, removeFav} from "../../redux/actions"
import  { connect } from "react-redux";
import { useState, useEffect } from "react";


function Card({id, name, species, gender, status, origin, image, onClose, addFav, removeFav, MyFavorites}) {//Aplicamos destructuring de props
   const [isFav, setIsFav] = useState(false);
   const handleFavorite =() =>{
         if(isFav){
            setIsFav(false);
            removeFav(id);
         }
         else{
            setIsFav(true);
            addFav({id, name, species, gender, status, origin, image})
         }

   }
useEffect(()=> {
   MyFavorites.forEach((fav)=>{
      if(fav.id === id){
         setIsFav(true);
   }
});
}, [MyFavorites]);

   return (//le pasamos al button la funcion Onclose pasada por props
      <div className={styles.container}>    
      {
         isFav ? (
            <button onClick={handleFavorite}>♥️</button>
         ):(
            <button onClick={handleFavorite}>♥️♥️</button>
         )
      }
      <div className={styles.card}>
            <div className={styles.content}>
            <button onClick={() => onClose(id)}>X</button>
      <NavLink className={styles.navlink} to ={`/detail/${id}`} >
          <img src={image} alt='{name}'/>
            <h3>{name}</h3>
      </NavLink> 
            <h3 >Specie: {species}</h3>
            <h3 >Gender:{gender}</h3>
            <h3 >Current Status: {status}</h3>
            <h3 >Known Origin: {origin}</h3>
         </div>
      </div>
      </div>
   );
}
const mapStateToProps = (state) =>{
   return{
      MyFavorites : state.MyFavorites
   }
}
const mapDispatchToProps =(dispatch) =>{
   return {
   addFav: (character) =>{dispatch(addFav(character))}, 
   removeFav:(id)=>{dispatch(removeFav(id))}
}
   }


export default connect(
   mapStateToProps,
   mapDispatchToProps
   )(Card);
